import React from 'react';
import NavBar from '../components/NavBar';
import Airtable from 'airtable';
/** The data dashboard page ... */





class DataDashboard extends React.Component {
    render(){
        // Airtable api call to get most recent entry
    function getLocation(){

        var Airtable = require('airtable');
        var base = new Airtable({apiKey: 'keypmQe98NRiZBQcw'}).base('appECiA8jbyRdHrbu');
        base('CRM-Prospects').select({
            // Selecting the first records in Contacts:
            maxRecords: 1,
            view: "Retrieve Most Recent"
        }).eachPage(function page(records, fetchNextPage) {
            // This function (`page`) will get called for each page of records.

            records.forEach(function(record) {
                return String(record.get("Venues"));
            });

        }, function done(err) {
            if (err) { console.error(err); return; }
        });
    }

    function getCount(){

        var Airtable = require('airtable');
        var base = new Airtable({apiKey: 'keypmQe98NRiZBQcw'}).base('appECiA8jbyRdHrbu');
        base('CRM-Prospects').select({
            // Selecting the first records in Contacts:
            maxRecords: 1,
            view: "Retrieve Most Recent"
        }).eachPage(function page(records, fetchNextPage) {
            // This function (`page`) will get called for each page of records.

            records.forEach(function(record) {
                return String(record.get("Test Count"));
            });

        }, function done(err) {
            if (err) { console.error(err); return; }
        });
    }
        let toPrint = getCount();
        console.log(toPrint)

        return(
            <div>
                <NavBar />
                <br></br>
                <h1 style={{color:"#ffffff"}}></h1>
                <div style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                }}>
                    
                    <p style={{color:"#ffffff"}}>{"Thank you for considering KOACORE for your safety needs. We have worked with events ranging from Elton John to Coldplay, and we cannot wait to add you to the KOA family. In your region, we have serviced " + getLocation() + " and provided " + getCount() + " tests for fans, generating $" + getCount() * 40 + " in shared revenue."} </p>
                </div>
            </div>
        );
    }
}

export default DataDashboard;
