import React from 'react';
import NavBar from '../components/NavBar';
import {Button} from 'react-bootstrap';
import Airtable from 'airtable';
import {Form} from 'react-bootstrap';
//import fs from 'fs';
//import {google} from 'googleapis';

/**
 * This is the calendar page with two calendars: a sales calendar and a event calendar
 * The calendars are next to each other so users can see both calendars in the same view
 * It includes a Google Calendar integration
 */

class CAL extends React.Component {
  render() {
    /** Calendar for existing events */
    return (
      <>
      <iframe src="https://airtable.com/embed/shrUeUoHKI6Tbowma?backgroundColor=purple&viewControls=on" width={1000} height={533} />
      </>
    );
  }
}

class ACTCAL extends React.Component {
    render() {
      /** Sales calendar */
      return (
        <>
        <iframe src="https://airtable.com/embed/shrFBCTmc1ADoYCEO?backgroundColor=purple&viewControls=on" width={1000} height={533} />
        </>
      );
    }
  }

class Calendar extends React.Component {

  state = {
    clientId: "",
    apiKey: "",
    discoveryDocs: [],
    scope: "",
    options: [
      {
        name: "Skyler",
        value: "Skyler Grandel (skylergrandel@gmail.com)"
      },
      {
        name: "Joe",
        value: "joeshmoe@gmail.com"
      }
    ],
    selectedOptions: []
  }

  componentDidMount = () => {
    //var base = new Airtable({apiKey: 'keypmQe98NRiZBQcw'}).base('appECiA8jbyRdHrbu');

    this.setState({
      clientId: "1075606334020-sdhvje80qvau18224tlqfb0g1gb5dqeb.apps.googleusercontent.com",
      apiKey: "AIzaSyDi6f8GTovPHez69kp77CvTSAEzSYBM__Q",
      discoveryDocs: ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"],
      scope: "https://www.googleapis.com/auth/calendar.events"
    });
  }

  onSelectedOptionsChange = (e) => {
    this.state.selectedOptions = e.target.value;
    console.log(this.state.selectedOptions);
  }

  handleClick = () => {
    var gapi = window.gapi;

    gapi.load('client:auth2', () => {
      console.log('loaded client');

      /*
      //not sure if we need gapi.client.setApiKey("AIzaSyDi6f8GTovPHez69kp77CvTSAEzSYBM__Q");

      // this causes an error because we are already signed in, but idk how else to set the
      // client's api key. For now it seems to work, but idk why.
      gapi.client.init({
        apiKey: "AIzaSyDi6f8GTovPHez69kp77CvTSAEzSYBM__Q",
        clientId: "1075606334020-sdhvje80qvau18224tlqfb0g1gb5dqeb.apps.googleusercontent.com",
        discoveryDocs: ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"],
        scope: "https://www.googleapis.com/auth/calendar.events",
      });
      */



      gapi.client.load('calendar', 'v3', () => console.log('bam!'));

      gapi.auth2.getAuthInstance().signIn()
      .then(() => {
        
        var event = {
          'summary': 'Awesome Event!',
          'description': 'Really great refreshments',
          'start': {
            'dateTime': '2022-04-28T09:00:00-07:00',
          },
          'end': {
            'dateTime': '2022-04-28T17:00:00-07:00',
          },
          /*
          'attendees': [
            {'email': 'lpage@example.com'},
            {'email': 'sbrin@example.com'}
          ],
          */
        };

        var request = gapi.client.calendar.events.insert({
          'calendarId': 'primary',
          'resource': event,
        });

        request.execute(event => {
          console.log(event)
          window.open(event.htmlLink)
        });
      });
    })
  }

  render(){
      return(
          <div>
              <NavBar employee={true} />
              <h1 style={{color:"#ffffff"}}>Calendar</h1>
              <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
              }}>
                  <h2 style={{margin:"auto", color:"#ffffff"}}>Events</h2>
                  <h2 style={{margin:"auto", color:"#ffffff"}}>Sales</h2>
              </div>
              <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
              }}>
                  <CAL />;
                  <ACTCAL />;
              </div>
              
              <Form>
                <Form.Control as="select" multiple value={this.state.selectedOptions} onChange={this.onSelectedOptionsChange}>
                  {this.state.options.map(option => (
                    <option key={option.name} value={option.value}>
                      {option.name}
                    </option>
                  ))}
                </Form.Control>
              </Form>
              <Button onClick={this.handleClick}> Add event to Google Calendar </Button>

          </div>
          
      );
  }
}

export default Calendar;
