import React from 'react';
import NavBar from '../components/NavBar';
import {Button} from 'react-bootstrap'
//import fs from 'fs';
//import {google} from 'googleapis';

/**
 * This is the calendar page with two calendars: a sales calendar and a event calendar
 * The calendars are next to each other so users can see both calendars in the same view
 * It includes a Google Calendar integration
 */

class CAL extends React.Component {
  render() {
    /** Calendar for existing events */
    return (
      <>
      <h2 style={{margin:"auto"}}>Events</h2>
      <iframe src="https://airtable.com/embed/shrUeUoHKI6Tbowma?backgroundColor=purple&viewControls=on" width={1000} height={533} />
      </>
    );
  }
}

class ACTCAL extends React.Component {
    render() {
      /** Sales calendar */
      return (
        <>
        <h2 style={{margin:"auto"}}>Events</h2>
        <iframe src="https://airtable.com/embed/shrFBCTmc1ADoYCEO?backgroundColor=purple&viewControls=on" width={1000} height={533} />
        </>
      );
    }
  }

class Calendar extends React.Component {

  state = {
    gapi: null,
    clientId: "",
    apiKey: "",
    discoveryDocs: [],
    scope: ""
  }

  componentDidMount = () => {
    this.setState({
      gapi: window.gapi,
      clientId: "1075606334020-sdhvje80qvau18224tlqfb0g1gb5dqeb.apps.googleusercontent.com",
      apiKey: "AIzaSyDi6f8GTovPHez69kp77CvTSAEzSYBM__Q",
      discoveryDocs: ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"],
      scope: "https://www.googleapis.com/auth/calendar.events"
    });
  }

  handleClick = () => {
    this.state.gapi.load('client:auth2', () => {
      console.log('loaded client')

      this.state.gapi.client.init({
        apiKey: this.state.apiKey,
        clientId: this.state.clientId,
        discoveryDocs: this.state.discoveryDocs,
        scope: this.state.scope,
      })

      this.state.gapi.client.load('calendar', 'v3', () => console.log('bam!'))

      this.state.gapi.auth2.getAuthInstance().signIn()
      .then(() => {
        
        var event = {
          'summary': 'Awesome Event!',
          'location': '800 Howard St., San Francisco, CA 94103',
          'description': 'Really great refreshments',
          'start': {
            'dateTime': '2020-06-28T09:00:00-07:00',
            'timeZone': 'America/Los_Angeles'
          },
          'end': {
            'dateTime': '2020-06-28T17:00:00-07:00',
            'timeZone': 'America/Los_Angeles'
          },
          'recurrence': [
            'RRULE:FREQ=DAILY;COUNT=2'
          ],
          'attendees': [
            {'email': 'lpage@example.com'},
            {'email': 'sbrin@example.com'}
          ],
          'reminders': {
            'useDefault': false,
            'overrides': [
              {'method': 'email', 'minutes': 24 * 60},
              {'method': 'popup', 'minutes': 10}
            ]
          }
        }

        var request = this.state.gapi.client.calendar.events.insert({
          'calendarId': 'primary',
          'resource': event,
        });

        request.execute(event => {
          console.log(event)
          window.open(event.htmlLink)
        });
      })
    })
  }

  render(){
      return(
          <div>
              <NavBar employee={true} />
              <h1 style={{color:"#ffffff"}}>Calendar</h1>
              <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
              }}>
                  <CAL />;
                  <ACTCAL />;
              </div>
              
              <Button onClick={this.handleClick}> Add event to Calendar </Button>

          </div>
          
      );
  }
}

export default Calendar;
