import React from 'react';
import NavBar from '../components/NavBar';
import {Button} from 'react-bootstrap'
//import fs from 'fs';
//import {google} from 'googleapis';

/**
 * This is the calendar page with two calendars: a sales calendar and a event calendar
 * The calendars are next to each other so users can see both calendars in the same view
 * It includes a Google Calendar integration
 */

class CAL extends React.Component {
  render() {
    /** Calendar for existing events */
    return (
      <>
      <h2 style={{margin:"auto"}}>Events</h2>
      <iframe src="https://airtable.com/embed/shrUeUoHKI6Tbowma?backgroundColor=purple&viewControls=on" width={1000} height={533} />
      </>
    );
  }
}

class ACTCAL extends React.Component {
    render() {
      /** Sales calendar */
      return (
        <>
        <h2 style={{margin:"auto"}}>Events</h2>
        <iframe src="https://airtable.com/embed/shrFBCTmc1ADoYCEO?backgroundColor=purple&viewControls=on" width={1000} height={533} />
        </>
      );
    }
  }

class Calendar extends React.Component {

  state = {
    gapi: window.gapi
  }
  
  componentDidMount = () => {
    this.state.gapi.load("client:auth2", function() {
      this.state.gapi.auth2.init({client_id: "1075606334020-sdhvje80qvau18224tlqfb0g1gb5dqeb.apps.googleusercontent.com"});
    });
  }

  authenticate = () => {
    return this.state.gapi.auth2.getAuthInstance()
        .signIn({scope: "https://www.googleapis.com/auth/calendar https://www.googleapis.com/auth/calendar.events"})
        .then(function() { console.log("Sign-in successful"); },
              function(err) { console.error("Error signing in", err); });
  }

  loadClient= () => {
    this.state.gapi.client.setApiKey("AIzaSyDi6f8GTovPHez69kp77CvTSAEzSYBM__Q");
    return this.state.gapi.client.load("https://content.googleapis.com/discovery/v1/apis/calendar/v3/rest")
        .then(function() { console.log("GAPI client loaded for API"); },
              function(err) { console.error("Error loading GAPI client for API", err); });
  }

  // Make sure the client is loaded and sign-in is complete before calling this method.
  execute = () => {
    return this.state.gapi.client.calendar.events.insert({
      "calendarId": "primary",
      "resource": {
        "end": {
          "date": "2022-04-23"
        },
        "start": {
          "date": "2022-04-23"
        }
      }
    })
        .then(function(response) {
                // Handle the results here (response.result has the parsed body).
                console.log("Response", response);
              },
              function(err) { console.error("Execute error", err); });
  }

  render(){
      return(
          <div>
              <NavBar employee={true} />
              <h1 style={{color:"#ffffff"}}>Calendar</h1>
              <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
              }}>
                  <CAL />;
                  <ACTCAL />;
              </div>
              
              <Button onClick={this.authenticate().then(this.loadClient).then(this.execute)}> Add event to Calendar </Button>

          </div>
          
      );
  }
}

export default Calendar;
